import React from "react";
import styled from "styled-components";

function About() {
  const Wrapper = styled.div`
    min-height: 70vh;
    background-color: aliceblue;

    img {
      width: 100%;
      height: 80%;
    }
  `;
  return (
    <Wrapper>
      <div className="container py-3">
        <div className="row">
          <div className="col-12">
            <img src="/assets/office.jpg" alt="office" />
            <p>NewVic head Office Abuja</p>
          </div>
        </div>
        <div className="row">
          <div className="col-12">
            <h4>
              Konga.com is Nigeria's largest online mall. We launched in July
              2012 and our mission is to become the engine of commerce and trade
              in Africa. We serve a retail customer base that continues to grow
              exponentially, offering products that span various categories
              including Phones, Computers, Clothing, Shoes, Home Appliances,
              Books, healthcare, Baby Products, personal care and much more. Our
              range of services are designed to ensure optimum levels of
              convenience and customer satisfaction with the retail process;
              these services include our lowest price guarantee, 7-day free
              return policy*, order delivery-tracking, dedicated customer
              service support and many other premium services. As we continue to
              expand the mall, our scope of offerings will increase in variety,
              simplicity and convenience; join us and enjoy the increasing
              benefits. We are highly customer-centric and are committed towards
              finding innovative ways of improving our customers' shopping
              experience with us; so give us some feedback on help@konga.com.
              For any press related questions, kindly send us an email at
              press@konga.com. Thank you and we hope you enjoy your experience
              with us.
            </h4>
          </div>
        </div>
      </div>
    </Wrapper>
  );
}

export default About;
